var ready;ready=function(){$("#event_time").timepicker({minTime:"5:00am",maxTime:"11:00pm"}),$(".join_event").click(function(){var e=$(event.target),t=e.closest("article").attr("id");$.post("/events/"+t+"/toggle_assistance",function(t){e.text(t.returned_state)},"json")}),$(".accept").click(function(){var e=$(event.target),t=e.closest("article").attr("id");$.post("/profile/"+t+"/accept_request",function(){$("#"+t).remove()},"json")}),$(".decline").click(function(){var e=$(event.target),t=e.closest("article").attr("id");$.post("/profile/"+t+"/decline_request",function(){$("#"+t).remove()},"json")})},$(document).ready(ready),$(document).on("page:load",ready);