var ready;ready=function(){$(".join_event").click(function(){var e=$(event.target),t=e.closest("article").attr("id");$.post("/events/"+t+"/toggle_assistance",function(o){"true"==$("#is_current_user_profile").text()?"Join"==o.returned_state&&$("#"+t).remove():e.text(o.returned_state)},"json")}),$("#follow").click(function(){var e=$("#user_id").text();$.post("/profile/"+e+"/toggle_follow",function(e){$("#follow").text(e.returned_state)},"json")})},$(document).ready(ready),$(document).on("page:load",ready);